import{_ as a,c as t,o as i,ah as l}from"./chunks/framework.DGDRXwgj.js";const g=JSON.parse('{"title":"Elemental Reactions","description":"","frontmatter":{"outline":2},"headers":[],"relativePath":"guide/elements/elemental_reactions.md","filePath":"guide/elements/elemental_reactions.md","lastUpdated":1759331139000}'),r={name:"guide/elements/elemental_reactions.md"};function n(o,e,s,h,c,m){return i(),t("div",null,[...e[0]||(e[0]=[l('<h1 id="elemental-reactions" tabindex="-1">Elemental Reactions <a class="header-anchor" href="#elemental-reactions" aria-label="Permalink to “Elemental Reactions”">​</a></h1><p>An <strong>Elemental Reaction</strong> is the result of combining two valid elements together! Each reaction has its own effect, which can be used to your advantage!</p><h2 id="transformative-reactions" tabindex="-1">Transformative Reactions <a class="header-anchor" href="#transformative-reactions" aria-label="Permalink to “Transformative Reactions”">​</a></h2><p><strong>Transformative Reactions</strong> are reactions that have their own instance of damage. This means that the target receives two <em>hits</em>: one from the attack, and one from the reaction.</p><p>The damage dealt by these reactions scale off the <code>levelMultiplier</code> game rule and their specific <em>Reaction Multiplier</em>. They also bypass Shields and the DMG cooldown, have no impact, deal no knockback and do not trigger the DMG cooldown timer.</p><h3 id="list" tabindex="-1">List <a class="header-anchor" href="#list" aria-label="Permalink to “List”">​</a></h3><ul><li><a href="./elemental_reactions/overloaded.html">Overloaded</a></li><li><a href="./elemental_reactions/electro_charged.html">Electro-Charged</a></li><li><a href="./elemental_reactions/superconduct.html">Superconduct</a></li><li><a href="./elemental_reactions/frozen.html#shatter">Shatter</a></li><li><a href="./elemental_reactions/swirl.html">Swirl</a></li><li><a href="./elemental_reactions/burning.html">Burning</a></li><li><a href="./elemental_reactions/bloom.html">Bloom</a></li><li><a href="./elemental_reactions/bloom.html#hyperbloom">Hyperbloom</a></li><li><a href="./elemental_reactions/bloom.html#burgeon">Burgeon</a></li></ul><h2 id="amplifying-reactions" tabindex="-1">Amplifying Reactions <a class="header-anchor" href="#amplifying-reactions" aria-label="Permalink to “Amplifying Reactions”">​</a></h2><p><strong>Amplifying Reactions</strong> are reactions that <strong>amplify</strong> the damage that triggered the reaction.</p><p>These reactions apply a <strong>multiplier</strong> on the damage that triggered the reaction, amplifying the damage further.</p><h3 id="list-1" tabindex="-1">List <a class="header-anchor" href="#list-1" aria-label="Permalink to “List”">​</a></h3><ul><li><a href="./elemental_reactions/melt.html">Melt</a></li><li><a href="./elemental_reactions/vaporize.html">Vaporize</a></li></ul><h2 id="additive-reactions" tabindex="-1">Additive Reactions <a class="header-anchor" href="#additive-reactions" aria-label="Permalink to “Additive Reactions”">​</a></h2><p><strong>Additive Reactions</strong> are reactions that <strong>add</strong> onto the damage that triggered the reaction.</p><p>These reactions apply an <strong>additive bonus</strong> that scales on the <code>levelMultiplier</code> game rule, applied as <strong>early</strong> as possible. For players, this is applied <strong>after</strong> the weapon damage and the <strong>1.5x</strong> CRIT hit multiplier.</p><h3 id="list-2" tabindex="-1">List <a class="header-anchor" href="#list-2" aria-label="Permalink to “List”">​</a></h3><ul><li><a href="./elemental_reactions/quicken.html#spread">Spread</a></li><li><a href="./elemental_reactions/quicken.html#aggravate">Aggravate</a></li></ul><h2 id="other-reactions" tabindex="-1">Other Reactions <a class="header-anchor" href="#other-reactions" aria-label="Permalink to “Other Reactions”">​</a></h2><p>These are the other reactions not classified as any of the above, and are usually ones that apply effects or other Elemental Auras that allow you to trigger a new set of reactions not triggerable with the standard seven Elements.</p><h3 id="list-3" tabindex="-1">List <a class="header-anchor" href="#list-3" aria-label="Permalink to “List”">​</a></h3><ul><li><a href="./elemental_reactions/frozen.html">Frozen</a></li><li><a href="./elemental_reactions/quicken.html">Quicken</a></li><li><a href="./elemental_reactions/crystallize.html">Crystallize</a></li></ul>',21)])])}const p=a(r,[["render",n]]);export{g as __pageData,p as default};
