import{_ as a,c as i,o as n,ah as t,aJ as s,aK as r,aL as l}from"./chunks/framework.DGDRXwgj.js";const b=JSON.parse('{"title":"Infusion Table","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"guide/workstations/infusion_table.md","filePath":"guide/workstations/infusion_table.md","lastUpdated":1759331139000}'),o={name:"guide/workstations/infusion_table.md"};function f(h,e,d,m,p,c){return n(),i("div",null,[...e[0]||(e[0]=[t('<h1 id="infusion-table" tabindex="-1">Infusion Table <a class="header-anchor" href="#infusion-table" aria-label="Permalink to “Infusion Table”">​</a></h1><hr><div style="display:flex;align-items:flex-start;gap:1rem;"><div style="flex:1;">The <b>Infusion Table</b> is a block used to spend <a href="https://minecraft.wiki/w/Experience">experience</a> to apply an elemental infusion to items!</div><img src="'+s+'" alt="..." style="width:150px;"></div><h2 id="obtaining" tabindex="-1">Obtaining <a class="header-anchor" href="#obtaining" aria-label="Permalink to “Obtaining”">​</a></h2><h3 id="breaking" tabindex="-1">Breaking <a class="header-anchor" href="#breaking" aria-label="Permalink to “Breaking”">​</a></h3><p>An infusion table requires an <a href="https://minecraft.wiki/w/Pickaxe" target="_blank" rel="noreferrer">iron pickaxe</a> or better to be mined, in which case it drops itself. Otherwise, it drops nothing.</p><h3 id="crafting" tabindex="-1">Crafting <a class="header-anchor" href="#crafting" aria-label="Permalink to “Crafting”">​</a></h3><p><img src="'+r+'" alt="Infusion Table Crafting Recipe" loading="lazy"></p><p>*<em>for Minecraft versions that don&#39;t have the specified items, i.e. <a href="https://minecraft.wiki/w/Wind_Charge" target="_blank" rel="noreferrer">Wind Charge</a> on 1.20.1, it is excluded from the recipe.</em></p><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to “Usage”">​</a></h2><div style="display:flex;align-items:flex-start;gap:1rem;"><p style="flex:1;"> An item can be infused by using an infusion table and placing the item in the centered input slot. Once an item is placed, the &quot;Infuse&quot; button is shown. <br> <br> To successfully infuse an item, the player must have at least 10 levels of experience. Otherwise, the Infuse button will appear disabled, and cannot be used. <br> <br> The infusion table is 1 <sup>1</sup>/<sub>4</sub> blocks high. </p><img src="'+l+'" alt="Infusion Table Interface" style="width:200px;"></div><h3 id="elemental-infusion" tabindex="-1">Elemental Infusion <a class="header-anchor" href="#elemental-infusion" aria-label="Permalink to “Elemental Infusion”">​</a></h3><p>    <em>Main page: <a href="./../elements/elemental_combat.html#elemental-infusion">Elemental Combat/Elemental Infusion</a></em></p><p>The infusion table&#39;s main purpose is to infuse items with the elements. The table can infuse <strong>all</strong> items with the elements.</p><p>If an item is already infused with an element, it is replaced with another random elemental infusion.</p>',15)])])}const g=a(o,[["render",f]]);export{b as __pageData,g as default};
